mapper.update:
update verifycode
set phoneNumber = #{phoneNumber},
  randomCode = #{randomCode},
  lastSendTime = #{lastSendTime},
  content = #{content}
where id = #{id}

mapper.select:
select id, phoneNumber, randomCode, lastSendTime, content
from verifycode
where id = #{id}

select id, phoneNumber, randomCode, lastSendTime, content
from verifycode
where phoneNumber = #{phoneNumber}

select id, phoneNumber, randomCode, lastSendTime, content
from verifycode
where randomCode = #{code}
