mapper.update:
update product_sku
set product_id = #{product.id,jdbcType=BIGINT},
  code = #{code,jdbcType=VARCHAR},
  price = #{price,jdbcType=DECIMAL},
  mods = #{mods,jdbcType=BIGINT}
where id = #{id}

mapper.select:
select id, product_id, code, price, mods
from product_sku
where id = #{id}
