mapper.update:
update sku_property
set name = #{name,jdbcType=VARCHAR},
  catalog_id = #{catalogId,jdbcType=BIGINT},
  type = #{type,jdbcType=TINYINT},
  sequence = #{sequence,jdbcType=INTEGER}
where id = #{id}

mapper.select:
select id, name, catalog_id, type, sequence
from sku_property
where id = #{id}
