mapper.update:
update menu
set text = #{text,jdbcType=VARCHAR},
  state = #{state,jdbcType=BIT},
  attributes = #{attributes,jdbcType=VARCHAR},
  parent_id = #{parentId,jdbcType=BIGINT}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, text, state, attributes, parent_id
from menu
where id = #{id,jdbcType=BIGINT}
