mapper.update:
update permission
set name = #{name,jdbcType=VARCHAR},
  resource = #{resource,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, name, resource
from permission
where id = #{id,jdbcType=BIGINT}

  	select * FROM permission where  id in (
		select p_id from role_permission where r_id = #{id}
	)
